mapper.select:
select 

from spider_record_info r, spider_source_scheduler_info s 
where to_days(now()) - to_days(date_format(r.create_time,"%Y-%m-%d")) >= #{0,jdbcType=INTEGER}
and to_days(now()) - to_days(date_format(r.create_time,"%Y-%m-%d")) < #{1,jdbcType=INTEGER} 
and s.sourceid = r.sourceid 
and s.status = 0

select 

from spider_source_scheduler_info r, spider_source_info s 
where to_days(now()) - to_days(date_format(r.create_time,"%Y-%m-%d")) >= #{0,jdbcType=INTEGER}
and to_days(now()) - to_days(date_format(r.create_time,"%Y-%m-%d")) < #{1,jdbcType=INTEGER} 
and s.sourceid = r.sourceid

select 

from ${param1} r, spider_source_scheduler_info s 
where to_days(now()) - to_days(date_format(r.update_time,"%Y-%m-%d")) >= ${param2}
and to_days(now()) - to_days(date_format(r.update_time,"%Y-%m-%d")) < ${param3} 
and s.sourceid = r.sourceid
